     * by making the `nonReentrant` function external, and making it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/AaveIntegration.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title AaveIntegration
 * @dev Contract to handle deposits and withdrawals from Aave lending pools
 */
contract AaveIntegration is ReentrancyGuard, Ownable, Pausable {

    // Aave position tracking
    struct AavePosition {
        uint256 depositedAmount;
        uint256 aTokenBalance;
        uint256 accruedInterest;
        uint256 lastInterestUpdate;
    }

    // State variables
    AavePosition public totalPosition;
    uint256 public totalDeposited;
    uint256 public totalInterestEarned;
    uint256 public lastInterestCalculation;

    // Authorized contracts that can call withdraw
    mapping(address => bool) public authorizedContracts;

    // Mock Aave interfaces for testing (will be replaced with actual Aave contracts)
    address public mockLendingPool;
    address public mockAToken;

    // Events
    event DepositedToAave(uint256 amount, uint256 timestamp);
    event WithdrawnFromAave(uint256 amount, uint256 timestamp);
    event InterestUpdated(uint256 newInterest, uint256 timestamp);
    event AaveContractsUpdated(address lendingPool, address aToken);
    event AuthorizedContractAdded(address indexed contractAddress);
    event AuthorizedContractRemoved(address indexed contractAddress);

    // Custom errors
    error InsufficientBalance();
    error InvalidAmount();
    error AaveOperationFailed();
    error ContractNotSet();

    constructor() Ownable(msg.sender) {
        lastInterestCalculation = block.timestamp;
    }

    /**
     * @dev Set Aave contract addresses (for testing and configuration)
     * @param _lendingPool Address of Aave lending pool
     * @param _aToken Address of aAVAX token
     */
    function setAaveContracts(address _lendingPool, address _aToken) external onlyOwner {
        mockLendingPool = _lendingPool;
        mockAToken = _aToken;
        emit AaveContractsUpdated(_lendingPool, _aToken);
    }

    /**
     * @dev Add authorized contract that can withdraw funds
     * @param contractAddress Address of the contract to authorize
     */
    function addAuthorizedContract(address contractAddress) external onlyOwner {
        authorizedContracts[contractAddress] = true;
        emit AuthorizedContractAdded(contractAddress);
    }

    /**
     * @dev Remove authorized contract
     * @param contractAddress Address of the contract to remove authorization
     */
    function removeAuthorizedContract(address contractAddress) external onlyOwner {
        authorizedContracts[contractAddress] = false;
        emit AuthorizedContractRemoved(contractAddress);
    }

    /**
     * @dev Deposit AVAX to Aave lending pool
     * @param amount Amount of AVAX to deposit
     */
    function depositToAave(uint256 amount) external payable virtual nonReentrant whenNotPaused {
        if (amount == 0 || msg.value != amount) {
            revert InvalidAmount();
        }

        // Update interest before new deposit
        _updateAccruedInterest();

        // For testing purposes, we'll simulate Aave deposit
        // In production, this would interact with actual Aave contracts
        _simulateAaveDeposit(amount);

        totalPosition.depositedAmount += amount;
        totalPosition.aTokenBalance += amount; // 1:1 ratio for simplicity
        totalDeposited += amount;

        emit DepositedToAave(amount, block.timestamp);
    }

    /**
     * @dev Withdraw AVAX from Aave lending pool
     * @param amount Amount of AVAX to withdraw
     */
    function withdrawFromAave(uint256 amount) external virtual nonReentrant whenNotPaused {
        if (amount == 0) {
            revert InvalidAmount();
        }

        if (amount > totalPosition.depositedAmount) {
            revert InsufficientBalance();
        }

        // Update interest before withdrawal
        _updateAccruedInterest();

        // For testing purposes, we'll simulate Aave withdrawal
        // In production, this would interact with actual Aave contracts
        uint256 withdrawnAmount = _simulateAaveWithdrawal(amount);

        totalPosition.depositedAmount -= amount;
        totalPosition.aTokenBalance -= amount;
        totalDeposited -= amount;

        // Transfer withdrawn amount to caller
        (bool success, ) = payable(msg.sender).call{value: withdrawnAmount}("");
        if (!success) {
            revert AaveOperationFailed();
        }

        emit WithdrawnFromAave(withdrawnAmount, block.timestamp);
    }

    /**
     * @dev Withdraw AVAX from Aave for authorized contracts (like StakingContract)
     * @param amount Amount of AVAX to withdraw
     * @param recipient Address to send the withdrawn funds to
     */
    function withdrawForContract(uint256 amount, address payable recipient) external virtual nonReentrant whenNotPaused {
        if (!authorizedContracts[msg.sender]) {
            revert AaveOperationFailed();
        }

        if (amount == 0) {
            revert InvalidAmount();
        }

        if (amount > totalPosition.depositedAmount) {
            revert InsufficientBalance();
        }

        // Update interest before withdrawal
        _updateAccruedInterest();

        // For testing purposes, we'll simulate Aave withdrawal
        uint256 withdrawnAmount = _simulateAaveWithdrawal(amount);

        totalPosition.depositedAmount -= amount;
        totalPosition.aTokenBalance -= amount;
        totalDeposited -= amount;

        // Ensure we don't try to send more than the contract balance
        uint256 actualWithdrawAmount = withdrawnAmount;
        if (actualWithdrawAmount > address(this).balance) {
            // If we don't have enough balance, just send what we have
            actualWithdrawAmount = address(this).balance;
        }

        // Transfer withdrawn amount to recipient
        if (actualWithdrawAmount > 0) {
            (bool success, ) = recipient.call{value: actualWithdrawAmount}("");
            if (!success) {
                revert AaveOperationFailed();
            }
        }

        emit WithdrawnFromAave(withdrawnAmount, block.timestamp);
    }

    /**
     * @dev Get current accrued interest from Aave
     * @return Current accrued interest amount
     */
    function getAccruedInterest() external view returns (uint256) {
        return _calculateCurrentInterest();
    }

    /**
     * @dev Get current aToken balance
     * @return Current aToken balance
     */
    function getATokenBalance() external view returns (uint256) {
        return totalPosition.aTokenBalance;
    }

    /**
     * @dev Get total deposited amount
     * @return Total amount deposited to Aave
     */
    function getTotalDeposited() external view returns (uint256) {
        return totalPosition.depositedAmount;
    }

    /**
     * @dev Get detailed Aave position information
     * @return AavePosition struct with all position details
     */
    function getAavePosition() external view returns (AavePosition memory) {
        AavePosition memory currentPosition = totalPosition;
        currentPosition.accruedInterest = _calculateCurrentInterest();
        return currentPosition;
    }

    /**
     * @dev Update accrued interest calculation
     */
    function updateInterest() external {
        _updateAccruedInterest();
    }

    /**
     * @dev Internal function to update accrued interest
     */
    function _updateAccruedInterest() internal {
        uint256 newInterest = _calculateCurrentInterest();
        totalPosition.accruedInterest = newInterest;
        totalPosition.lastInterestUpdate = block.timestamp;
        totalInterestEarned = newInterest;
        lastInterestCalculation = block.timestamp;

        emit InterestUpdated(newInterest, block.timestamp);
    }

    /**
     * @dev Calculate current interest based on time elapsed and deposit amount
     * @return Calculated interest amount
     */
    function _calculateCurrentInterest() internal view virtual returns (uint256) {
        if (totalPosition.depositedAmount == 0) {
            return totalPosition.accruedInterest;
        }

        uint256 timeElapsed = block.timestamp - lastInterestCalculation;
        // Simulate 5% APY (simplified calculation for testing)
        // In production, this would query actual Aave interest rates
        uint256 annualRate = 5; // 5%
        uint256 secondsInYear = 365 * 24 * 60 * 60;

        uint256 newInterest = (totalPosition.depositedAmount * annualRate * timeElapsed) / (100 * secondsInYear);
        return totalPosition.accruedInterest + newInterest;
    }

    /**
     * @dev Simulate Aave deposit (for testing purposes)
     * @param amount Amount to deposit
     */
    function _simulateAaveDeposit(uint256 amount) internal {
        // In production, this would call:
        // ILendingPool(mockLendingPool).deposit(asset, amount, address(this), 0);
        // For now, we just hold the AVAX in this contract
    }

    /**
     * @dev Simulate Aave withdrawal (for testing purposes)
     * @param amount Amount to withdraw
     * @return Actual amount withdrawn (including interest)
     */
    function _simulateAaveWithdrawal(uint256 amount) internal view returns (uint256) {
        // In production, this would call:
        // ILendingPool(mockLendingPool).withdraw(asset, amount, address(this));

        // Calculate proportional interest to include in withdrawal
        uint256 interestShare = 0;
        if (totalPosition.depositedAmount > 0) {
            interestShare = (totalPosition.accruedInterest * amount) / totalPosition.depositedAmount;
        }

        return amount + interestShare;
    }

    /**
     * @dev Emergency withdrawal function (only owner)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        if (amount > address(this).balance) {
            revert InsufficientBalance();
        }

        (bool success, ) = payable(owner()).call{value: amount}("");
        if (!success) {
            revert AaveOperationFailed();
        }
    }

    /**
     * @dev Pause contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get contract balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Receive function to accept AVAX deposits
     */
    receive() external payable virtual {}
}

contracts/RealAaveIntegration.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

import "./AaveIntegration.sol"; // inherit to remain type-compatible with StakingContract

// Aave v3 interfaces
import { IPoolAddressesProvider } from "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import { IPool } from "@aave/core-v3/contracts/interfaces/IPool.sol";
// Minimal interface to avoid extra dependency
interface IWrappedTokenGatewayV3 {
    function depositETH(address pool, address onBehalfOf, uint16 referralCode) external payable;
    function withdrawETH(address pool, uint256 amount, address to) external;
}
import { DataTypes } from "@aave/core-v3/contracts/protocol/libraries/types/DataTypes.sol";

/**
 * @title RealAaveIntegration
 * @dev Aave v3-backed implementation compatible with AaveIntegration type.
 *      Uses WrappedNativeGatewayV3 to deposit/withdraw native AVAX to WAVAX reserve on Aave.
 */
contract RealAaveIntegration is AaveIntegration {
    IPool public pool;
    IWrappedTokenGatewayV3 public gateway;
    IERC20 public aToken;          // aToken for WAVAX reserve
    address public reserveAsset;   // WAVAX token address (underlying)

    event AaveConfigured(address provider, address gateway, address reserveAsset, address aToken);

    constructor(
        address addressesProvider,
        address wrappedNativeGateway,
        address reserveAsset_
    ) AaveIntegration() {
        // Initialize Aave pool/gateway
        pool = IPool(IPoolAddressesProvider(addressesProvider).getPool());
        gateway = IWrappedTokenGatewayV3(wrappedNativeGateway);
        reserveAsset = reserveAsset_;
        // Derive aToken address from reserve data on-chain
        DataTypes.ReserveData memory rd = pool.getReserveData(reserveAsset);
        aToken = IERC20(rd.aTokenAddress);

        emit AaveConfigured(addressesProvider, wrappedNativeGateway, reserveAsset_, address(aToken));
    }

    /**
     * @dev Override: deposit native AVAX via gateway into Aave WAVAX reserve
     */
    function depositToAave(uint256 amount) external payable override nonReentrant whenNotPaused {
        if (amount == 0 || msg.value != amount) revert InvalidAmount();

        // Update accrued interest accounting based on current aToken before deposit
        _updateAccruedInterest();

        // Deposit native AVAX using the gateway
        gateway.depositETH{value: amount}(address(pool), address(this), 0);

        // Update internal accounting based on aToken balance
        totalPosition.depositedAmount += amount;
        totalPosition.aTokenBalance = aToken.balanceOf(address(this));
        totalDeposited += amount;

        emit DepositedToAave(amount, block.timestamp);
    }

    /**
     * @dev Override: withdraw native AVAX via gateway back to caller
     */
    function withdrawFromAave(uint256 amount) external override nonReentrant whenNotPaused {
        if (amount == 0) revert InvalidAmount();
        if (amount > totalPosition.depositedAmount) revert InsufficientBalance();

        _updateAccruedInterest();

        // Withdraw native AVAX using the gateway; sends to this contract
        // We request 'amount'; any interest stays reflected in aToken balance reduction
        gateway.withdrawETH(address(pool), amount, address(this));

        // Update internal accounting
        totalPosition.depositedAmount -= amount;
        totalPosition.aTokenBalance = aToken.balanceOf(address(this));
        totalDeposited -= amount;

        // Transfer withdrawn amount to caller
        (bool ok, ) = payable(msg.sender).call{value: amount}("");
        if (!ok) revert AaveOperationFailed();

        emit WithdrawnFromAave(amount, block.timestamp);
    }

    /**
     * @dev Override: authorized withdrawal for another contract (e.g., StakingContract)
     */
    function withdrawForContract(uint256 amount, address payable recipient) external override nonReentrant whenNotPaused {
        if (!authorizedContracts[msg.sender]) revert AaveOperationFailed();
        if (amount == 0) revert InvalidAmount();
        if (amount > totalPosition.depositedAmount) revert InsufficientBalance();

        _updateAccruedInterest();

        gateway.withdrawETH(address(pool), amount, address(this));

        totalPosition.depositedAmount -= amount;
        totalPosition.aTokenBalance = aToken.balanceOf(address(this));
        totalDeposited -= amount;

        uint256 sendAmount = amount;
        if (sendAmount > address(this).balance) {
            sendAmount = address(this).balance;
        }
        if (sendAmount > 0) {
            (bool ok, ) = recipient.call{value: sendAmount}("");
            if (!ok) revert AaveOperationFailed();
        }

        emit WithdrawnFromAave(sendAmount, block.timestamp);
    }

    /**
     * @dev Override: compute current interest from aToken growth over principal
     */
    function _calculateCurrentInterest() internal view override returns (uint256) {
        // Actual interest = current aToken balance - principal deposited (bounded)
        uint256 currentATokenBal = aToken.balanceOf(address(this));
        if (currentATokenBal <= totalPosition.depositedAmount) {
            return 0; // no interest yet
        }
        return currentATokenBal - totalPosition.depositedAmount;
    }

    // Accept native withdrawals
    receive() external payable override {}
}

contracts/RewardDistribution.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./StakingContract.sol";
import "./AaveIntegration.sol";

/**
 * @title RewardDistribution
 * @dev Contract to calculate and distribute rewards based on Aave yield and staking positions
 */
contract RewardDistribution is ReentrancyGuard, Ownable, Pausable {

    struct RewardPool {
        uint256 totalRewards;
        uint256 distributedRewards;
        uint256 lastUpdateTime;
        uint256 rewardRate;
        uint256 accumulatedRewardPerToken;
    }

    struct UserReward {
        uint256 totalClaimed;
        uint256 lastClaimTime;
    }

    // State variables
    StakingContract public stakingContract;
    AaveIntegration public aaveIntegration;
    RewardPool public rewardPool;

    // User reward tracking
    mapping(address => UserReward) public userRewards;
    mapping(address => uint256) public userLastStakeTime;

    // Reward distribution parameters
    uint256 public constant REWARD_PRECISION = 1e18;
    uint256 public minimumClaimAmount;
    uint256 public rewardUpdateInterval;
    uint256 public lastRewardUpdate;

    // Events
    event RewardsUpdated(uint256 newRewards, uint256 timestamp);
    event RewardsClaimed(address indexed user, uint256 amount, uint256 timestamp);
    event RewardPoolUpdated(uint256 totalRewards, uint256 rewardRate);
    event ContractsSet(address stakingContract, address aaveIntegration);
    event MinimumClaimAmountUpdated(uint256 newAmount);

    // Custom errors
    error ContractsNotSet();
    error InsufficientRewards();
    error NoRewardsToClaim();
    error InvalidAmount();
    error RewardUpdateTooSoon();

    constructor(uint256 _minimumClaimAmount, uint256 _rewardUpdateInterval) Ownable(msg.sender) {
        minimumClaimAmount = _minimumClaimAmount;
        rewardUpdateInterval = _rewardUpdateInterval;
        lastRewardUpdate = block.timestamp;

        rewardPool = RewardPool({
            totalRewards: 0,
            distributedRewards: 0,
            lastUpdateTime: block.timestamp,
            rewardRate: 0,
            accumulatedRewardPerToken: 0
        });
    }

    /**
     * @dev Set the staking and Aave integration contracts
     * @param _stakingContract Address of the staking contract
     * @param _aaveIntegration Address of the Aave integration contract
     */
    function setContracts(address _stakingContract, address _aaveIntegration) external onlyOwner {
        stakingContract = StakingContract(payable(_stakingContract));
        aaveIntegration = AaveIntegration(payable(_aaveIntegration));
        emit ContractsSet(_stakingContract, _aaveIntegration);
    }

    /**
     * @dev Update rewards based on current Aave interest
     */
    function updateRewards() external nonReentrant whenNotPaused {
        if (address(stakingContract) == address(0) || address(aaveIntegration) == address(0)) {
            revert ContractsNotSet();
        }

        _updateRewardPool();
        emit RewardsUpdated(rewardPool.totalRewards, block.timestamp);
    }

    /**
     * @dev Calculate user's current reward amount
     * @param user Address of the user
     * @return Current reward amount for the user
     */
    function calculateUserReward(address user) external view returns (uint256) {
        return _calculateUserReward(user);
    }

    /**
     * @dev Claim rewards for the calling user
     */
    function claimRewards() external nonReentrant whenNotPaused {
        _updateRewardPool();

        uint256 reward = _calculateUserReward(msg.sender);

        if (reward == 0) {
            revert NoRewardsToClaim();
        }

        if (reward < minimumClaimAmount) {
            revert InsufficientRewards();
        }

        // Check if contract has sufficient balance
        if (address(this).balance < reward) {
            revert InsufficientRewards();
        }

        // Update user's claim tracking
        userRewards[msg.sender].totalClaimed += reward;
        userRewards[msg.sender].lastClaimTime = block.timestamp;

        // Update distributed rewards
        rewardPool.distributedRewards += reward;

        // Transfer reward to user
        (bool success, ) = payable(msg.sender).call{value: reward}("");
        if (!success) {
            revert InsufficientRewards();
        }

        emit RewardsClaimed(msg.sender, reward, block.timestamp);
    }

    /**
     * @dev Get current reward rate (rewards per second per token)
     * @return Current reward rate
     */
    function getRewardRate() external view returns (uint256) {
        return rewardPool.rewardRate;
    }

    /**
     * @dev Get total available rewards
     * @return Total rewards available for distribution
     */
    function getTotalRewards() external view returns (uint256) {
        return rewardPool.totalRewards;
    }

    /**
     * @dev Get total distributed rewards
     * @return Total rewards that have been distributed
     */
    function getDistributedRewards() external view returns (uint256) {
        return rewardPool.distributedRewards;
    }

    /**
     * @dev Get user's reward information
     * @param user Address of the user
     * @return UserReward struct with user's reward details
     */
    function getUserRewardInfo(address user) external view returns (UserReward memory) {
        return userRewards[user];
    }

    /**
     * @dev Get user's current claimable reward amount
     * @param user Address of the user
     * @return Current claimable reward amount
     */
    function getUserClaimableReward(address user) external view returns (uint256) {
        return _calculateUserReward(user);
    }

    /**
     * @dev Internal function to update the reward pool
     */
    function _updateRewardPool() internal {
        if (address(aaveIntegration) == address(0)) {
            return;
        }

        // Get current interest from Aave
        uint256 currentInterest = aaveIntegration.getAccruedInterest();

        // Simply update the total rewards to current interest
        rewardPool.totalRewards = currentInterest;
        rewardPool.lastUpdateTime = block.timestamp;
        lastRewardUpdate = block.timestamp;

        // Calculate reward rate based on time elapsed
        uint256 timeDelta = block.timestamp - rewardPool.lastUpdateTime;
        if (timeDelta > 0 && currentInterest > 0) {
            rewardPool.rewardRate = currentInterest / timeDelta;
        }

        emit RewardPoolUpdated(rewardPool.totalRewards, rewardPool.rewardRate);
    }

    /**
     * @dev Internal function to update user's reward (simplified)
     * @param user Address of the user
     */
    function _updateUserReward(address user) internal {
        // This function is now simplified since we calculate rewards on-demand
        // Just update the last interaction time if needed
    }

    /**
     * @dev Internal function to calculate user's current reward
     * @param user Address of the user
     * @return Current reward amount for the user
     */
    function _calculateUserReward(address user) internal view returns (uint256) {
        uint256 userStaked = stakingContract.userTotalStaked(user);

        if (userStaked == 0) {
            return 0;
        }

        // Get current interest from Aave
        uint256 currentInterest = 0;
        if (address(aaveIntegration) != address(0)) {
            currentInterest = aaveIntegration.getAccruedInterest();
        }

        uint256 totalStaked = stakingContract.totalStaked();
        if (totalStaked == 0) {
            return 0;
        }

        // Calculate user's proportional share of total interest
        uint256 userTotalShare = (currentInterest * userStaked) / totalStaked;

        // Subtract what they've already claimed
        uint256 alreadyClaimed = userRewards[user].totalClaimed;

        // Return claimable amount (total share minus already claimed)
        return userTotalShare > alreadyClaimed ? userTotalShare - alreadyClaimed : 0;
    }

    /**
     * @dev Notify when user stakes (called by staking contract)
     * @param user Address of the user who staked
     */
    function notifyStake(address user) external {
        if (msg.sender != address(stakingContract)) {
            return; // Only staking contract can call this
        }

        _updateRewardPool();
        _updateUserReward(user);
        userLastStakeTime[user] = block.timestamp;
    }

    /**
     * @dev Notify when user unstakes (called by staking contract)
     * @param user Address of the user who unstaked
     */
    function notifyUnstake(address user) external {
        if (msg.sender != address(stakingContract)) {
            return; // Only staking contract can call this
        }

        _updateRewardPool();
        _updateUserReward(user);
    }

    /**
     * @dev Emergency function to withdraw rewards (only owner)
     * @param amount Amount to withdraw
     */
    function emergencyWithdrawRewards(uint256 amount) external onlyOwner {
        if (amount > address(this).balance) {
            revert InsufficientRewards();
        }

        (bool success, ) = payable(owner()).call{value: amount}("");
        if (!success) {
            revert InsufficientRewards();
        }
    }

    /**
     * @dev Update minimum claim amount (only owner)
     * @param _minimumClaimAmount New minimum claim amount
     */
    function setMinimumClaimAmount(uint256 _minimumClaimAmount) external onlyOwner {
        minimumClaimAmount = _minimumClaimAmount;
        emit MinimumClaimAmountUpdated(_minimumClaimAmount);
    }

    /**
     * @dev Pause contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get contract balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Receive function to accept AVAX deposits for rewards
     */
    receive() external payable {}
}

contracts/StakingContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./AaveIntegration.sol";

// Forward declaration to avoid circular dependency
interface IRewardDistribution {
    function notifyStake(address user) external;
    function notifyUnstake(address user) external;
}

/**
 * @title StakingContract
 * @dev Core staking contract for Avalanche L1 blockchain with position tracking
 */
contract StakingContract is ReentrancyGuard, Ownable, Pausable {

    struct StakingPosition {
        uint256 id;
        address staker;
        uint256 amount;
        uint256 startTime;
        uint256 lockPeriod;
        uint256 lastRewardClaim;
        bool active;
    }

    // State variables
    uint256 private nextPositionId;
    uint256 public totalStaked;
    uint256 public minimumStakeAmount;
    uint256 public maximumLockPeriod;
    uint256 public minimumLockPeriod;

    // Aave integration
    AaveIntegration public aaveIntegration;

    // Reward distribution integration
    IRewardDistribution public rewardDistribution;

    // Mappings
    mapping(address => uint256[]) public userPositions;
    mapping(uint256 => StakingPosition) public stakingPositions;
    mapping(address => uint256) public userTotalStaked;

    // Events
    event Staked(
        address indexed staker,
        uint256 indexed positionId,
        uint256 amount,
        uint256 lockPeriod,
        uint256 timestamp
    );

    event Unstaked(
        address indexed staker,
        uint256 indexed positionId,
        uint256 amount,
        uint256 timestamp
    );

    event EmergencyWithdraw(
        address indexed staker,
        uint256 indexed positionId,
        uint256 amount,
        uint256 penalty
    );

    event AaveIntegrationSet(address indexed aaveIntegration);
    event TokensDepositedToAave(uint256 amount);
    event TokensWithdrawnFromAave(uint256 amount);
    event RewardDistributionSet(address indexed rewardDistribution);

    // Custom errors
    error InsufficientStakeAmount();
    error InvalidLockPeriod();
    error PositionNotFound();
    error PositionNotActive();
    error LockPeriodNotExpired();
    error UnauthorizedAccess();
    error InsufficientBalance();
    error AaveIntegrationNotSet();
    error AaveOperationFailed();

    constructor(
        uint256 _minimumStakeAmount,
        uint256 _minimumLockPeriod,
        uint256 _maximumLockPeriod
    ) Ownable(msg.sender) {
        minimumStakeAmount = _minimumStakeAmount;
        minimumLockPeriod = _minimumLockPeriod;
        maximumLockPeriod = _maximumLockPeriod;
        nextPositionId = 1;
    }

    /**
     * @dev Stake AVAX tokens for a specified lock period
     * @param lockPeriod Duration to lock tokens (in seconds)
     */
    function stake(uint256 lockPeriod) external payable nonReentrant whenNotPaused {
        if (msg.value < minimumStakeAmount) {
            revert InsufficientStakeAmount();
        }

        if (lockPeriod < minimumLockPeriod || lockPeriod > maximumLockPeriod) {
            revert InvalidLockPeriod();
        }

        uint256 positionId = nextPositionId++;

        StakingPosition memory newPosition = StakingPosition({
            id: positionId,
            staker: msg.sender,
            amount: msg.value,
            startTime: block.timestamp,
            lockPeriod: lockPeriod,
            lastRewardClaim: block.timestamp,
            active: true
        });

        stakingPositions[positionId] = newPosition;
        userPositions[msg.sender].push(positionId);
        userTotalStaked[msg.sender] += msg.value;
        totalStaked += msg.value;

        // Automatically deposit to Aave if integration is set
        if (address(aaveIntegration) != address(0)) {
            try aaveIntegration.depositToAave{value: msg.value}(msg.value) {
                emit TokensDepositedToAave(msg.value);
            } catch {
                // If Aave deposit fails, continue with staking but emit error
                // In production, you might want to revert here
            }
        }

        // Notify reward distribution contract
        if (address(rewardDistribution) != address(0)) {
            try rewardDistribution.notifyStake(msg.sender) {
                // Successfully notified reward distribution
            } catch {
                // Continue even if notification fails
            }
        }

        emit Staked(msg.sender, positionId, msg.value, lockPeriod, block.timestamp);
    }

    /**
     * @dev Unstake tokens after lock period expires
     * @param positionId ID of the staking position to unstake
     */
    function unstake(uint256 positionId) external nonReentrant whenNotPaused {
        StakingPosition storage position = stakingPositions[positionId];

        if (position.staker != msg.sender) {
            revert UnauthorizedAccess();
        }

        if (!position.active) {
            revert PositionNotActive();
        }

        if (block.timestamp < position.startTime + position.lockPeriod) {
            revert LockPeriodNotExpired();
        }

        uint256 amount = position.amount;
        position.active = false;

        userTotalStaked[msg.sender] -= amount;
        totalStaked -= amount;

        uint256 withdrawAmount = amount;
        uint256 contractBalanceBefore = address(this).balance;

        // Withdraw from Aave if integration is set
        if (address(aaveIntegration) != address(0)) {
            try aaveIntegration.withdrawForContract(amount, payable(address(this))) {
                emit TokensWithdrawnFromAave(amount);
                // After successful withdrawal, calculate actual amount received
                uint256 balanceReceived = address(this).balance - contractBalanceBefore;
                withdrawAmount = balanceReceived > 0 ? balanceReceived : amount;
            } catch {
                // If Aave withdrawal fails, use available balance
                withdrawAmount = address(this).balance >= amount ? amount : address(this).balance;
            }
        } else {
            // Use available contract balance for withdrawal
            withdrawAmount = address(this).balance >= amount ? amount : address(this).balance;
        }

        // Ensure we don't try to send more than we have
        if (withdrawAmount > address(this).balance) {
            withdrawAmount = address(this).balance;
        }

        // Notify reward distribution contract before transfer
        if (address(rewardDistribution) != address(0)) {
            try rewardDistribution.notifyUnstake(msg.sender) {
                // Successfully notified reward distribution
            } catch {
                // Continue even if notification fails
            }
        }

        // Transfer amount back to user
        if (withdrawAmount > 0) {
            (bool success, ) = payable(msg.sender).call{value: withdrawAmount}("");
            if (!success) {
                revert InsufficientBalance();
            }
        } else {
            revert InsufficientBalance();
        }

        emit Unstaked(msg.sender, positionId, withdrawAmount, block.timestamp);
    }

    /**
     * @dev Emergency unstake with penalty (for testing purposes)
     * @param positionId ID of the staking position to emergency unstake
     */
    function emergencyUnstake(uint256 positionId) external nonReentrant whenNotPaused {
        StakingPosition storage position = stakingPositions[positionId];

        if (position.staker != msg.sender) {
            revert UnauthorizedAccess();
        }

        if (!position.active) {
            revert PositionNotActive();
        }

        uint256 amount = position.amount;
        uint256 penalty = amount / 10; // 10% penalty
        uint256 withdrawAmount = amount - penalty;

        position.active = false;
        userTotalStaked[msg.sender] -= amount;
        totalStaked -= amount;

        // Ensure we don't try to send more than the contract balance
        uint256 contractBalance = address(this).balance;
        if (withdrawAmount > contractBalance) {
            withdrawAmount = contractBalance;
        }

        // Transfer reduced amount back to user
        if (withdrawAmount > 0) {
            (bool success, ) = payable(msg.sender).call{value: withdrawAmount}("");
            if (!success) {
                revert InsufficientBalance();
            }
        }

        emit EmergencyWithdraw(msg.sender, positionId, withdrawAmount, penalty);
    }

    /**
     * @dev Get all staking positions for a user
     * @param user Address of the user
     * @return Array of StakingPosition structs
     */
    function getStakingPositions(address user) external view returns (StakingPosition[] memory) {
        uint256[] memory positionIds = userPositions[user];
        StakingPosition[] memory positions = new StakingPosition[](positionIds.length);

        for (uint256 i = 0; i < positionIds.length; i++) {
            positions[i] = stakingPositions[positionIds[i]];
        }

        return positions;
    }

    /**
     * @dev Get active staking positions for a user
     * @param user Address of the user
     * @return Array of active StakingPosition structs
     */
    function getActiveStakingPositions(address user) external view returns (StakingPosition[] memory) {
        uint256[] memory positionIds = userPositions[user];
        uint256 activeCount = 0;

        // Count active positions
        for (uint256 i = 0; i < positionIds.length; i++) {
            if (stakingPositions[positionIds[i]].active) {
                activeCount++;
            }
        }

        // Create array of active positions
        StakingPosition[] memory activePositions = new StakingPosition[](activeCount);
        uint256 index = 0;

        for (uint256 i = 0; i < positionIds.length; i++) {
            if (stakingPositions[positionIds[i]].active) {
                activePositions[index] = stakingPositions[positionIds[i]];
                index++;
            }
        }

        return activePositions;
    }

    /**
     * @dev Check if a position can be unstaked
     * @param positionId ID of the staking position
     * @return Boolean indicating if position can be unstaked
     */
    function canUnstake(uint256 positionId) external view returns (bool) {
        StakingPosition memory position = stakingPositions[positionId];
        return position.active && (block.timestamp >= position.startTime + position.lockPeriod);
    }

    /**
     * @dev Get remaining lock time for a position
     * @param positionId ID of the staking position
     * @return Remaining lock time in seconds (0 if unlocked)
     */
    function getRemainingLockTime(uint256 positionId) external view returns (uint256) {
        StakingPosition memory position = stakingPositions[positionId];

        if (!position.active) {
            return 0;
        }

        uint256 unlockTime = position.startTime + position.lockPeriod;
        if (block.timestamp >= unlockTime) {
            return 0;
        }

        return unlockTime - block.timestamp;
    }

    /**
     * @dev Set Aave integration contract (only owner)
     * @param _aaveIntegration Address of the Aave integration contract
     */
    function setAaveIntegration(address _aaveIntegration) external onlyOwner {
        aaveIntegration = AaveIntegration(payable(_aaveIntegration));
        emit AaveIntegrationSet(_aaveIntegration);
    }

    /**
     * @dev Set reward distribution contract (only owner)
     * @param _rewardDistribution Address of the reward distribution contract
     */
    function setRewardDistribution(address _rewardDistribution) external onlyOwner {
        rewardDistribution = IRewardDistribution(_rewardDistribution);
        emit RewardDistributionSet(_rewardDistribution);
    }

    /**
     * @dev Get current rewards from Aave integration
     * @return Total accrued interest from Aave
     */
    function getAaveRewards() external view returns (uint256) {
        if (address(aaveIntegration) == address(0)) {
            return 0;
        }
        return aaveIntegration.getAccruedInterest();
    }

    /**
     * @dev Test function to withdraw from Aave (for testing purposes only)
     * @param amount Amount to withdraw
     */
    function testAaveWithdrawal(uint256 amount) external onlyOwner {
        if (address(aaveIntegration) != address(0)) {
            aaveIntegration.withdrawForContract(amount, payable(address(this)));
            emit TokensWithdrawnFromAave(amount);
        }
    }

    /**
     * @dev Update staking parameters (only owner)
     */
    function updateStakingParameters(
        uint256 _minimumStakeAmount,
        uint256 _minimumLockPeriod,
        uint256 _maximumLockPeriod
    ) external onlyOwner {
        minimumStakeAmount = _minimumStakeAmount;
        minimumLockPeriod = _minimumLockPeriod;
        maximumLockPeriod = _maximumLockPeriod;
    }

    /**
     * @dev Pause contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get contract balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Receive function to accept AVAX deposits from Aave withdrawals
     */
    receive() external payable {}
}

