contracts/AaveIntegration.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title AaveIntegration
 * @dev Contract to handle deposits and withdrawals from Aave lending pools
 */
contract AaveIntegration is ReentrancyGuard, Ownable, Pausable {

    // Aave position tracking
    struct AavePosition {
        uint256 depositedAmount;
        uint256 aTokenBalance;
        uint256 accruedInterest;
        uint256 lastInterestUpdate;
    }

    // State variables
    AavePosition public totalPosition;
    uint256 public totalDeposited;
    uint256 public totalInterestEarned;
    uint256 public lastInterestCalculation;

    // Authorized contracts that can call withdraw
    mapping(address => bool) public authorizedContracts;

    // Mock Aave interfaces for testing (will be replaced with actual Aave contracts)
    address public mockLendingPool;
    address public mockAToken;

    // Events
    event DepositedToAave(uint256 amount, uint256 timestamp);
    event WithdrawnFromAave(uint256 amount, uint256 timestamp);
    event InterestUpdated(uint256 newInterest, uint256 timestamp);
    event AaveContractsUpdated(address lendingPool, address aToken);
    event AuthorizedContractAdded(address indexed contractAddress);
    event AuthorizedContractRemoved(address indexed contractAddress);

    // Custom errors
    error InsufficientBalance();
    error InvalidAmount();
    error AaveOperationFailed();
    error ContractNotSet();

    constructor() Ownable(msg.sender) {
        lastInterestCalculation = block.timestamp;
    }

    /**
     * @dev Set Aave contract addresses (for testing and configuration)
     * @param _lendingPool Address of Aave lending pool
     * @param _aToken Address of aAVAX token
     */
    function setAaveContracts(address _lendingPool, address _aToken) external onlyOwner {
        mockLendingPool = _lendingPool;
        mockAToken = _aToken;
        emit AaveContractsUpdated(_lendingPool, _aToken);
    }

    /**
     * @dev Add authorized contract that can withdraw funds
     * @param contractAddress Address of the contract to authorize
     */
    function addAuthorizedContract(address contractAddress) external onlyOwner {
        authorizedContracts[contractAddress] = true;
        emit AuthorizedContractAdded(contractAddress);
    }

    /**
     * @dev Remove authorized contract
     * @param contractAddress Address of the contract to remove authorization
     */
    function removeAuthorizedContract(address contractAddress) external onlyOwner {
        authorizedContracts[contractAddress] = false;
        emit AuthorizedContractRemoved(contractAddress);
    }

    /**
     * @dev Deposit AVAX to Aave lending pool
     * @param amount Amount of AVAX to deposit
     */
    function depositToAave(uint256 amount) external payable virtual nonReentrant whenNotPaused {
        if (amount == 0 || msg.value != amount) {
            revert InvalidAmount();
        }

        // Update interest before new deposit
        _updateAccruedInterest();

        // For testing purposes, we'll simulate Aave deposit
        // In production, this would interact with actual Aave contracts
        _simulateAaveDeposit(amount);

        totalPosition.depositedAmount += amount;
        totalPosition.aTokenBalance += amount; // 1:1 ratio for simplicity
        totalDeposited += amount;

        emit DepositedToAave(amount, block.timestamp);
    }

    /**
     * @dev Withdraw AVAX from Aave lending pool
     * @param amount Amount of AVAX to withdraw
     */
    function withdrawFromAave(uint256 amount) external virtual nonReentrant whenNotPaused {
        if (amount == 0) {
            revert InvalidAmount();
        }

        if (amount > totalPosition.depositedAmount) {
            revert InsufficientBalance();
        }

        // Update interest before withdrawal
        _updateAccruedInterest();

        // For testing purposes, we'll simulate Aave withdrawal
        // In production, this would interact with actual Aave contracts
        uint256 withdrawnAmount = _simulateAaveWithdrawal(amount);

        totalPosition.depositedAmount -= amount;
        totalPosition.aTokenBalance -= amount;
        totalDeposited -= amount;

        // Transfer withdrawn amount to caller
        (bool success, ) = payable(msg.sender).call{value: withdrawnAmount}("");
        if (!success) {
            revert AaveOperationFailed();
        }

        emit WithdrawnFromAave(withdrawnAmount, block.timestamp);
    }

    /**
     * @dev Withdraw AVAX from Aave for authorized contracts (like StakingContract)
     * @param amount Amount of AVAX to withdraw
     * @param recipient Address to send the withdrawn funds to
     */
    function withdrawForContract(uint256 amount, address payable recipient) external virtual nonReentrant whenNotPaused {
        if (!authorizedContracts[msg.sender]) {
            revert AaveOperationFailed();
        }

        if (amount == 0) {
            revert InvalidAmount();
        }

        if (amount > totalPosition.depositedAmount) {
            revert InsufficientBalance();
        }

        // Update interest before withdrawal
        _updateAccruedInterest();

        // For testing purposes, we'll simulate Aave withdrawal
        uint256 withdrawnAmount = _simulateAaveWithdrawal(amount);

        totalPosition.depositedAmount -= amount;
        totalPosition.aTokenBalance -= amount;
        totalDeposited -= amount;

        // Ensure we don't try to send more than the contract balance
        uint256 actualWithdrawAmount = withdrawnAmount;
        if (actualWithdrawAmount > address(this).balance) {
            // If we don't have enough balance, just send what we have
            actualWithdrawAmount = address(this).balance;
        }

        // Transfer withdrawn amount to recipient
        if (actualWithdrawAmount > 0) {
            (bool success, ) = recipient.call{value: actualWithdrawAmount}("");
            if (!success) {
                revert AaveOperationFailed();
            }
        }

        emit WithdrawnFromAave(withdrawnAmount, block.timestamp);
    }

    /**
     * @dev Get current accrued interest from Aave
     * @return Current accrued interest amount
     */
    function getAccruedInterest() external view returns (uint256) {
        return _calculateCurrentInterest();
    }

    /**
     * @dev Get current aToken balance
     * @return Current aToken balance
     */
    function getATokenBalance() external view returns (uint256) {
        return totalPosition.aTokenBalance;
    }

    /**
     * @dev Get total deposited amount
     * @return Total amount deposited to Aave
     */
    function getTotalDeposited() external view returns (uint256) {
        return totalPosition.depositedAmount;
    }

    /**
     * @dev Get detailed Aave position information
     * @return AavePosition struct with all position details
     */
    function getAavePosition() external view returns (AavePosition memory) {
        AavePosition memory currentPosition = totalPosition;
        currentPosition.accruedInterest = _calculateCurrentInterest();
        return currentPosition;
    }

    /**
     * @dev Update accrued interest calculation
     */
    function updateInterest() external {
        _updateAccruedInterest();
    }

    /**
     * @dev Internal function to update accrued interest
     */
    function _updateAccruedInterest() internal {
        uint256 newInterest = _calculateCurrentInterest();
        totalPosition.accruedInterest = newInterest;
        totalPosition.lastInterestUpdate = block.timestamp;
        totalInterestEarned = newInterest;
        lastInterestCalculation = block.timestamp;

        emit InterestUpdated(newInterest, block.timestamp);
    }

    /**
     * @dev Calculate current interest based on time elapsed and deposit amount
     * @return Calculated interest amount
     */
    function _calculateCurrentInterest() internal view virtual returns (uint256) {
        if (totalPosition.depositedAmount == 0) {
            return totalPosition.accruedInterest;
        }

        uint256 timeElapsed = block.timestamp - lastInterestCalculation;
        // Simulate 5% APY (simplified calculation for testing)
        // In production, this would query actual Aave interest rates
        uint256 annualRate = 5; // 5%
        uint256 secondsInYear = 365 * 24 * 60 * 60;

        uint256 newInterest = (totalPosition.depositedAmount * annualRate * timeElapsed) / (100 * secondsInYear);
        return totalPosition.accruedInterest + newInterest;
    }

    /**
     * @dev Simulate Aave deposit (for testing purposes)
     * @param amount Amount to deposit
     */
    function _simulateAaveDeposit(uint256 amount) internal {
        // In production, this would call:
        // ILendingPool(mockLendingPool).deposit(asset, amount, address(this), 0);
        // For now, we just hold the AVAX in this contract
    }

    /**
     * @dev Simulate Aave withdrawal (for testing purposes)
     * @param amount Amount to withdraw
     * @return Actual amount withdrawn (including interest)
     */
    function _simulateAaveWithdrawal(uint256 amount) internal view returns (uint256) {
        // In production, this would call:
        // ILendingPool(mockLendingPool).withdraw(asset, amount, address(this));

        // Calculate proportional interest to include in withdrawal
        uint256 interestShare = 0;
        if (totalPosition.depositedAmount > 0) {
            interestShare = (totalPosition.accruedInterest * amount) / totalPosition.depositedAmount;
        }

        return amount + interestShare;
    }

    /**
     * @dev Emergency withdrawal function (only owner)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        if (amount > address(this).balance) {
            revert InsufficientBalance();
        }

        (bool success, ) = payable(owner()).call{value: amount}("");
        if (!success) {
            revert AaveOperationFailed();
        }
    }

    /**
     * @dev Pause contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get contract balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Receive function to accept AVAX deposits
     */
    receive() external payable virtual {}
}

contracts/EpochPrizePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./AaveIntegration.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/**
 * @title EpochPrizePool
 * @notice Epoch-based staking/prize pool with automatic epoch close and
 *         exponential-decay winner weighting. Users deposit AVAX during an epoch;
 *         at epoch end, the total principal plus net yield is distributed to the
 *         top M stakers by stake size with exponential rank weights.
 *
 * Winner-takes-principal variant: non-winners forfeit their stake (no payout).
 */
contract EpochPrizePool is ReentrancyGuard, Ownable, Pausable {
    using EnumerableSet for EnumerableSet.AddressSet;

    // ---------------------------- Config ----------------------------
    AaveIntegration public aave;

    uint256 public epochDuration;        // seconds
    uint256 public feeBps;               // fee on yield only, in basis points

    // Platform fee escrowed in this contract; owner can withdraw
    uint256 public feeEscrowTotal;

    uint256 public numWinners;           // M
    uint256 public decayBps;             // decay factor d in bps (e.g., 6000 => 0.6)

    // ---------------------------- Epoch state ----------------------------
    struct Epoch {
        uint64 startTime;
        uint64 endTime;
        bool closed;
        bool finalized;
        uint256 totalStaked;     // S
        uint256 yieldAmount;     // Y for this epoch (delta vs baseline)
        uint256 feeAmount;       // fee on Y
        uint256 netReward;       // R = Y - fee
        uint256 prizePool;       // P = S + R
        uint256 interestBaseline; // snapshot of aave accruedInterest at epoch start
    }
    // Trusted signer (backend) that attests the winners list for an epoch
    address public winnerSigner;


    // epochId starts from 1
    uint256 public currentEpochId;
    mapping(uint256 => Epoch) public epochs;

    // Per-epoch staking balances and participant set
    mapping(uint256 => mapping(address => uint256)) public stakeOf; // epochId => user => amount
    mapping(uint256 => EnumerableSet.AddressSet) private _participants; // epochId => set of users
    // Per-epoch backend-provided winners (in rank order)
    mapping(uint256 => address[]) public epochWinners;
    mapping(uint256 => bool) public winnersSet;


    // Claimable allocations per epoch (for winners)
    mapping(uint256 => mapping(address => uint256)) public allocationOf; // epochId => user => prize amount

    // ---------------------------- Events ----------------------------
    event ConfigUpdated(uint256 epochDuration, uint256 feeBps, uint256 numWinners, uint256 decayBps);
    event AaveIntegrationSet(address indexed aave);
    event WinnerSignerUpdated(address indexed signer);
    event EpochStarted(uint256 indexed epochId, uint64 startTime, uint64 endTime);
    event Deposited(address indexed user, uint256 indexed epochId, uint256 amount);
    event EpochClosed(uint256 indexed epochId, uint256 S, uint256 Y, uint256 fee, uint256 R, uint256 P);
    event EpochFinalized(uint256 indexed epochId);
    event WinnersSet(uint256 indexed epochId, address[] winners);
    event WinnerPaid(address indexed user, uint256 indexed epochId, uint256 amount);
    event Claimed(address indexed user, uint256 indexed epochId, uint256 amount);
    event FeeWithdrawn(address indexed to, uint256 amount);

    // ---------------------------- Errors ----------------------------
    error InvalidParam();
    error EpochNotOpen();
    error EpochNotFinalized();
    error NothingToClaim();

    constructor(
        AaveIntegration _aave,
        uint256 _epochDuration,
        uint256 _feeBps,
        uint256 _numWinners,
        uint256 _decayBps
    ) Ownable(msg.sender) {
        setAaveIntegration(_aave);
        configure(_epochDuration, _feeBps, _numWinners, _decayBps);
        _startNextEpoch();
    }

    // ---------------------------- Admin ----------------------------

    function setAaveIntegration(AaveIntegration _aave) public onlyOwner {
        if (address(_aave) == address(0)) revert InvalidParam();
        aave = _aave;
        emit AaveIntegrationSet(address(_aave));
    }

    function configure(
        uint256 _epochDuration,
        uint256 _feeBps,
        uint256 _numWinners,
        uint256 _decayBps
    ) public onlyOwner {
        if (_epochDuration == 0 || _feeBps > 10_000 || _numWinners == 0 || _decayBps > 10_000 || _decayBps == 0) {
            revert InvalidParam();
        }
        epochDuration = _epochDuration;
        feeBps = _feeBps;
        numWinners = _numWinners;
        decayBps = _decayBps;
        emit ConfigUpdated(_epochDuration, _feeBps, _numWinners, _decayBps);
    }

    function setWinnerSigner(address signer) external onlyOwner {
        if (signer == address(0)) revert InvalidParam();
        winnerSigner = signer;
        emit WinnerSignerUpdated(signer);
    }


    // Backend submits winners with signature: signer signs keccak256(abi.encode(
    //    epochId, winners[]
    // ))
    function submitWinners(
        uint256 epochId,
        address[] calldata winners,
        bytes calldata signature
    ) external whenNotPaused {
        Epoch storage ep = epochs[epochId];
        require(ep.startTime != 0, "bad epoch");
        require(!winnersSet[epochId], "already set");
        require(winners.length > 0, "no winners");

        // Verify signer (eth_sign over keccak256(abi.encode(...)))
        bytes32 digest = keccak256(abi.encode(address(this), epochId, winners));
        bytes32 ethHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", digest));
        address recovered = ECDSA.recover(ethHash, signature);
        require(recovered == winnerSigner, "bad sig");

        // Store winners in rank order, capped to numWinners
        uint256 m = winners.length;
        if (m > numWinners) m = numWinners;
        address[] storage arr = epochWinners[epochId];
        for (uint256 i = 0; i < m; i++) {
            require(winners[i] != address(0), "zero winner");
            arr.push(winners[i]);
        }
        winnersSet[epochId] = true;
        emit WinnersSet(epochId, winners);
    }


    function pause() external onlyOwner { _pause(); }
    // Owner can withdraw accumulated platform fee (escrow)
    function withdrawFees(uint256 amount, address payable to) external onlyOwner nonReentrant {
        if (amount == 0 || amount > feeEscrowTotal || to == address(0)) revert InvalidParam();
        feeEscrowTotal -= amount;
        (bool ok, ) = to.call{value: amount}("");
        require(ok, "fee withdraw failed");
        emit FeeWithdrawn(to, amount);
    }

    function unpause() external onlyOwner { _unpause(); }

    // ---------------------------- Public user actions ----------------------------

    // Payable deposit of AVAX into the current epoch; auto-closes/finalizes when due
    function deposit() external payable nonReentrant whenNotPaused {
        _autoCloseAndFinalizeIfDue();
        Epoch storage ep = epochs[currentEpochId];
        if (block.timestamp >= ep.endTime || ep.closed) revert EpochNotOpen();
        if (msg.value == 0) revert InvalidParam();

        // Track user stake for this epoch
        if (_participants[currentEpochId].add(msg.sender)) {
            // added new participant
        }
        stakeOf[currentEpochId][msg.sender] += msg.value;
        ep.totalStaked += msg.value;

        // Forward to Aave
        aave.depositToAave{value: msg.value}(msg.value);

        emit Deposited(msg.sender, currentEpochId, msg.value);
    }

    // Claim allocated prize for a finalized epoch
    function claim(uint256 epochId) external nonReentrant whenNotPaused {
        _autoCloseAndFinalizeIfDue();
        if (!epochs[epochId].finalized) revert EpochNotFinalized();
        uint256 amt = allocationOf[epochId][msg.sender];
        if (amt == 0) revert NothingToClaim();
        allocationOf[epochId][msg.sender] = 0;
        (bool ok, ) = payable(msg.sender).call{value: amt}("");
        require(ok, "transfer failed");
        emit WinnerPaid(msg.sender, epochId, amt);
        emit Claimed(msg.sender, epochId, amt);
    }

    // Anyone can poke to close/finalize if due
    function poke() external {
        _autoCloseAndFinalizeIfDue();
    }

    // ---------------------------- Internal epoch lifecycle ----------------------------

    function _startNextEpoch() internal {
        // Update interest then snapshot baseline for the new epoch
        aave.updateInterest();
        uint256 baseline = aave.getAccruedInterest();

        currentEpochId += 1;
        uint64 start = uint64(block.timestamp);
        uint64 end = uint64(start + epochDuration);
        epochs[currentEpochId] = Epoch({
            startTime: start,
            endTime: end,
            closed: false,
            finalized: false,
            totalStaked: 0,
            yieldAmount: 0,
            feeAmount: 0,
            netReward: 0,
            prizePool: 0,
            interestBaseline: baseline
        });
        emit EpochStarted(currentEpochId, start, end);
    }

    function _autoCloseAndFinalizeIfDue() internal {
        Epoch storage ep = epochs[currentEpochId];
        if (!ep.closed && block.timestamp >= ep.endTime) {
            _closeEpoch();
        }
        if (ep.closed && !ep.finalized) {
            _finalizeEpoch();
        }
    }

    // Close epoch: snapshot pool math (Y, fee, R, P). Does not move funds.
    function _closeEpoch() internal {
        Epoch storage ep = epochs[currentEpochId];
        if (ep.closed) return;

        // Update and snapshot interest delta for this epoch
        aave.updateInterest();
        AaveIntegration.AavePosition memory pos = aave.getAavePosition();
        uint256 S = ep.totalStaked;
        uint256 accumulated = pos.accruedInterest;
        uint256 Y = accumulated > ep.interestBaseline ? (accumulated - ep.interestBaseline) : 0;
        uint256 fee = (Y * feeBps) / 10_000;
        uint256 R = Y > fee ? (Y - fee) : 0;
        uint256 P = S + R;

        ep.yieldAmount = Y;
        ep.feeAmount = fee;
        ep.netReward = R;
        ep.prizePool = P;
        ep.closed = true;

        emit EpochClosed(currentEpochId, S, Y, fee, R, P);
    }

    // Finalize epoch: withdraw from Aave, compute winners, assign allocations, send fee, start next epoch
    function _finalizeEpoch() internal nonReentrant {
        Epoch storage ep = epochs[currentEpochId];
        // Require winners list to be set by backend signer before prize assignment
        require(winnersSet[currentEpochId], "winners not set");

        if (!ep.closed || ep.finalized) return;

        // Update interest and withdraw full principal to this contract
        aave.updateInterest();
        uint256 principalOutstanding = aave.getTotalDeposited();
        if (principalOutstanding > 0) {
            aave.withdrawForContract(principalOutstanding, payable(address(this)));
        }

        // Escrow the fee in-contract for owner withdrawal
        if (ep.feeAmount > 0) {
            feeEscrowTotal += ep.feeAmount;
        }

        // Compute winners and allocations based on exponential decay weights combined with stake size
        _assignPrizes(ep.prizePool);

        ep.finalized = true;
        emit EpochFinalized(currentEpochId);

        // Start next epoch immediately
        _startNextEpoch();
    }

    // Allocate prize pool using backend-provided winners (rank order) + exponential decay weighting and stake weighting
    function _assignPrizes(uint256 P) internal {
        uint256 eid = currentEpochId;
        address[] storage winners = epochWinners[eid];
        uint256 m = winners.length;
        if (m == 0 || P == 0) return;

        // Exponential raw weights
        uint256[] memory raw = new uint256[](m);
        uint256 cur = 1e18;
        for (uint256 j = 0; j < m; j++) {
            raw[j] = cur;
            cur = (cur * decayBps) / 10_000;
        }

        // Compute W = sum(raw[j] * stake[winner_j])
        uint256 W = 0;
        uint256[] memory stakes = new uint256[](m);
        for (uint256 j2 = 0; j2 < m; j2++) {
            address w = winners[j2];
            uint256 s = stakeOf[eid][w];
            stakes[j2] = s;
            W += (raw[j2] * s);
        }
        if (W == 0) return;

        // Allocate prizes
        for (uint256 j3 = 0; j3 < m; j3++) {
            uint256 prize = (P * (raw[j3] * stakes[j3])) / W;
            allocationOf[eid][winners[j3]] += prize;
        }
    }

    // ---------------------------- Views & helpers ----------------------------
    function getParticipants(uint256 epochId) external view returns (address[] memory addrs) {
        EnumerableSet.AddressSet storage setRef = _participants[epochId];
        uint256 n = setRef.length();
        addrs = new address[](n);
        for (uint256 i = 0; i < n; i++) {
            addrs[i] = setRef.at(i);
        }
    }

    function getEpoch(uint256 epochId) external view returns (Epoch memory) {
        return epochs[epochId];
    }

    // Receive native AVAX (from Aave withdrawals)
    receive() external payable {}
}

